  1→import { NextRequest, NextResponse } from 'next/server';
  2→import { headers } from 'next/headers';
  3→import Stripe from 'stripe';
  4→import { prisma } from '@/lib/prisma';
  5→
  6→const stripe = new Stripe(process.env.STRIPE_SECRET_KEY as string, {
  7→  apiVersion: '2025-05-28.basil',
  8→});
  9→
 10→const webhookSecret = process.env.STRIPE_WEBHOOK_SECRET as string;
 11→
 12→export async function POST(req: NextRequest) {
 13→  const body = await req.text();
 14→  const signature = headers().get('stripe-signature') as string;
 15→
 16→  let event: Stripe.Event;
 17→
 18→  try {
 19→    event = stripe.webhooks.constructEvent(body, signature, webhookSecret);
 20→  } catch (err) {
 21→    console.error('Webhook signature verification failed:', err);
 22→    return NextResponse.json({ error: 'Webhook signature verification failed' }, { status: 400 });
 23→  }
 24→
 25→  try {
