  1→import { Resend } from 'resend';
  2→import { captureException } from './sentry';
  3→
  4→// Initialize Resend with API key from environment
  5→const resend = new Resend(process.env.RESEND_API_KEY);
  6→
  7→export interface EmailParams {
  8→  to: string | string[];
  9→  subject: string;
 10→  html?: string;
 11→  text?: string;
 12→  from?: string;
 13→  replyTo?: string;
 14→  cc?: string | string[];
 15→  bcc?: string | string[];
 16→  attachments?: Array<{
 17→    filename: string;
 18→    content: Buffer | string;
 19→    contentType?: string;
 20→  }>;
 21→  tags?: Array<{
 22→    name: string;
 23→    value: string;
 24→  }>;
 25→}
 26→
 27→export interface EmailResponse {
 28→  success: boolean;
 29→  messageId?: string;
 30→  error?: string;
 31→}
 32→
 33→const DEFAULT_FROM = process.env.SMTP_FROM || 'noreply@zenith.engineer';
 34→
 35→export async function sendEmail(params: EmailParams): Promise<EmailResponse> {
 36→  try {
 37→    // Validate required parameters
 38→    if (!params.to) {
 39→      throw new Error('Recipient email address is required');
 40→    }
 41→    
 42→    if (!params.subject) {
 43→      throw new Error('Email subject is required');
 44→    }
 45→    
 46→    if (!params.html && !params.text) {
 47→      throw new Error('Email content (html or text) is required');
 48→    }
 49→
 50→    // Check if Resend is properly configured
 51→    if (!process.env.RESEND_API_KEY) {
 52→      console.warn('RESEND_API_KEY not configured, email will not be sent');
 53→      return { success: false, error: 'Email service not configured' };
 54→    }
 55→
 56→    // Send email using Resend
 57→    const response = await resend.emails.send({
 58→      from: params.from || DEFAULT_FROM,
 59→      to: Array.isArray(params.to) ? params.to : [params.to],
 60→      subject: params.subject,
 61→      html: params.html,
 62→      text: params.text,
 63→      reply_to: params.replyTo,
 64→      cc: params.cc ? (Array.isArray(params.cc) ? params.cc : [params.cc]) : undefined,
 65→      bcc: params.bcc ? (Array.isArray(params.bcc) ? params.bcc : [params.bcc]) : undefined,
 66→      attachments: params.attachments,
 67→      tags: params.tags,
 68→    });
 69→
 70→    if (response.error) {
 71→      throw new Error(`Resend API error: ${response.error.message}`);
 72→    }
 73→
 74→    console.log(`Email sent successfully to ${params.to}:`, response.data?.id);
 75→    
 76→    return {
 77→      success: true,
 78→      messageId: response.data?.id,
 79→    };
 80→  } catch (error) {
 81→    const errorMessage = error instanceof Error ? error.message : 'Unknown email error';
 82→    
 83→    console.error('Failed to send email:', {
 84→      error: errorMessage,
 85→      to: params.to,
 86→      subject: params.subject,
 87→    });
 88→
 89→    // Capture error in Sentry
 90→    captureException(error as Error, {
 91→      context: 'email-service',
 92→      extra: {
 93→        to: params.to,
 94→        subject: params.subject,
 95→        hasHtml: !!params.html,
 96→        hasText: !!params.text,
 97→      },
 98→    });
 99→
100→    return {
101→      success: false,
102→      error: errorMessage,
103→    };
104→  }
105→}
