  1→import { BetaAnalyticsDataClient } from '@google-analytics/data';
  2→import { AnalyticsAdminServiceClient } from '@google-analytics/admin';
  3→import { google } from 'googleapis';
  4→import { captureException } from './sentry';
  5→
  6→// Google Analytics Configuration
  7→interface GAConfig {
  8→  propertyId: string;
  9→  measurementId: string;
 10→  serviceAccount?: {
 11→    client_email: string;
 12→    private_key: string;
 13→    project_id: string;
 14→  };
 15→}
 16→
 17→class GoogleAnalyticsService {
 18→  private dataClient: BetaAnalyticsDataClient | null = null;
 19→  private adminClient: AnalyticsAdminServiceClient | null = null;
 20→  private analytics: any = null;
 21→  private config: GAConfig;
 22→
 23→  constructor() {
 24→    this.config = {
 25→      propertyId: process.env.GA_PROPERTY_ID || '',
 26→      measurementId: process.env.NEXT_PUBLIC_GA_MEASUREMENT_ID || '',
 27→      serviceAccount: process.env.GOOGLE_SERVICE_ACCOUNT_KEY ? 
 28→        JSON.parse(process.env.GOOGLE_SERVICE_ACCOUNT_KEY) : undefined,
 29→    };
 30→
 31→    this.initializeClients();
 32→  }
 33→
 34→  private initializeClients() {
 35→    try {
 36→      if (this.config.serviceAccount) {
 37→        // Initialize with service account
 38→        const credentials = {
 39→          client_email: this.config.serviceAccount.client_email,
 40→          private_key: this.config.serviceAccount.private_key.replace(/\\n/g, '\n'),
 41→        };
 42→
 43→        this.dataClient = new BetaAnalyticsDataClient({
 44→          credentials,
 45→          projectId: this.config.serviceAccount.project_id,
 46→        });
 47→
 48→        this.adminClient = new AnalyticsAdminServiceClient({
 49→          credentials,
 50→          projectId: this.config.serviceAccount.project_id,
 51→        });
 52→
 53→        // Initialize Google APIs client
 54→        const auth = new google.auth.GoogleAuth({
 55→          credentials,
 56→          scopes: [
 57→            'https://www.googleapis.com/auth/analytics.readonly',
 58→            'https://www.googleapis.com/auth/analytics.edit',
 59→            'https://www.googleapis.com/auth/analytics.manage.users',
 60→          ],
 61→        });
 62→
 63→        this.analytics = google.analytics({ version: 'v3', auth });
 64→      }
 65→    } catch (error) {
 66→      console.error('Failed to initialize Google Analytics clients:', error);
 67→      captureException(error as Error, { context: 'google-analytics-init' });
 68→    }
 69→  }
 70→
 71→  // Real-time Analytics Data
 72→  async getRealtimeData() {
 73→    try {
 74→      if (!this.dataClient || !this.config.propertyId) {
 75→        throw new Error('Google Analytics not properly configured');
 76→      }
 77→
 78→      const [response] = await this.dataClient.runRealtimeReport({
 79→        property: `properties/${this.config.propertyId}`,
 80→        dimensions: [
 81→          { name: 'country' },
 82→          { name: 'deviceCategory' },
 83→        ],
 84→        metrics: [
 85→          { name: 'activeUsers' },
 86→          { name: 'screenPageViews' },
 87→        ],
 88→      });
 89→
 90→      return {
 91→        success: true,
 92→        data: response,
 93→        activeUsers: response.totals?.[0]?.metricValues?.[0]?.value || '0',
 94→        pageViews: response.totals?.[0]?.metricValues?.[1]?.value || '0',
 95→      };
 96→    } catch (error) {
 97→      console.error('Failed to get realtime data:', error);
 98→      captureException(error as Error, { context: 'ga-realtime-data' });
 99→      return { success: false, error: (error as Error).message };
100→    }
101→  }
102→
103→  // Historical Analytics Data
104→  async getAnalyticsData(startDate: string, endDate: string) {
105→    try {
106→      if (!this.dataClient || !this.config.propertyId) {
107→        throw new Error('Google Analytics not properly configured');
108→      }
