 21→class GoogleCloudStorageService {
 22→  private storage: Storage;
 23→  private bucketName: string;
 24→
 25→  constructor() {
 26→    this.bucketName = process.env.GCS_BUCKET_NAME || 'zenith-fresh';
 27→    
 28→    // Initialize Google Cloud Storage
 29→    this.storage = new Storage({
 30→      projectId: process.env.GOOGLE_CLOUD_PROJECT_ID || 'green-diagram-463408-n5',
 31→      keyFilename: process.env.GOOGLE_CLOUD_KEY_FILE, // Path to service account JSON
 32→      credentials: process.env.GOOGLE_CLOUD_CREDENTIALS ? 
 33→        JSON.parse(process.env.GOOGLE_CLOUD_CREDENTIALS) : undefined,
 34→    });
 35→  }
 36→
 37→  /**
 38→   * Upload a file to Google Cloud Storage
 39→   */
 40→  async uploadFile(config: UploadConfig): Promise<UploadResult> {
 41→    try {
 42→      const bucket = this.storage.bucket(config.bucketName || this.bucketName);
 43→      const file = bucket.file(config.fileName);
 44→
 45→      // Create write stream
 46→      const stream = file.createWriteStream({
 47→        metadata: {
 48→          contentType: config.mimeType || 'application/octet-stream',
 49→          metadata: config.metadata || {},
 50→        },
 51→        public: config.isPublic || false,
 52→        validation: 'md5',
 53→      });
 54→
 55→      return new Promise((resolve) => {
 56→        stream.on('error', (error) => {
 57→          console.error('Upload error:', error);
 58→          captureException(error, { context: 'gcs-upload' });
 59→          resolve({
 60→            success: false,
 61→            error: error.message,
 62→          });
 63→        });
 64→
 65→        stream.on('finish', async () => {
 66→          try {
 67→            let publicUrl: string | undefined;
 68→            let signedUrl: string | undefined;
 69→
 70→            if (config.isPublic) {
 71→              // Make file public and get public URL
 72→              await file.makePublic();
 73→              publicUrl = `https://storage.googleapis.com/${config.bucketName || this.bucketName}/${config.fileName}`;
 74→            } else {
 75→              // Generate signed URL for private files (valid for 1 hour)
 76→              const [url] = await file.getSignedUrl({
 77→                action: 'read',
 78→                expires: Date.now() + 60 * 60 * 1000, // 1 hour
 79→              });
 80→              signedUrl = url;
 81→            }
 82→
 83→            resolve({
 84→              success: true,
 85→              fileName: config.fileName,
 86→              publicUrl,
 87→              signedUrl,
 88→            });
 89→          } catch (error) {
 90→            console.error('Post-upload error:', error);
 91→            captureException(error as Error, { context: 'gcs-post-upload' });
 92→            resolve({
 93→              success: false,
 94→              error: (error as Error).message,
 95→            });
 96→          }
 97→        });
 98→
 99→        // Write the buffer to the stream
100→        stream.end(config.buffer);
101→      });
102→    } catch (error) {
103→      console.error('GCS upload failed:', error);
104→      captureException(error as Error, { context: 'gcs-upload-init' });
105→      return {
106→        success: false,
107→        error: (error as Error).message,
108→      };
109→    }
110→  }
111→
112→  /**
113→   * Delete a file from Google Cloud Storage
114→   */
115→  async deleteFile(fileName: string, bucketName?: string): Promise<{ success: boolean; error?: string }> {
116→    try {
117→      const bucket = this.storage.bucket(bucketName || this.bucketName);
118→      const file = bucket.file(fileName);
119→
120→      await file.delete();
121→
122→      return { success: true };
123→    } catch (error) {
124→      console.error('GCS delete failed:', error);
125→      captureException(error as Error, { context: 'gcs-delete' });
126→      return {
127→        success: false,
128→        error: (error as Error).message,
129→      };
130→    }
131→  }
132→
133→  /**
134→   * Get file metadata
135→   */
136→  async getFileMetadata(fileName: string, bucketName?: string) {
137→    try {
138→      const bucket = this.storage.bucket(bucketName || this.bucketName);
139→      const file = bucket.file(fileName);
140→
141→      const [metadata] = await file.getMetadata();
142→
143→      return {
144→        success: true,
145→        metadata,
146→      };
147→    } catch (error) {
148→      console.error('GCS metadata failed:', error);
149→      captureException(error as Error, { context: 'gcs-metadata' });
150→      return {
151→        success: false,
152→        error: (error as Error).message,
153→      };
154→    }
155→  }
156→
157→  /**
158→   * Generate signed URL for file access
159→   */
160→  async getSignedUrl(
161→    fileName: string, 
162→    action: 'read' | 'write' = 'read',
163→    expiresInHours: number = 1,
164→    bucketName?: string
165→  ) {
166→    try {
167→      const bucket = this.storage.bucket(bucketName || this.bucketName);
168→      const file = bucket.file(fileName);
169→
170→      const [signedUrl] = await file.getSignedUrl({
171→        action,
172→        expires: Date.now() + expiresInHours * 60 * 60 * 1000,
173→      });
174→
175→      return {
176→        success: true,
