import { Resend } from 'resend';
import { captureException } from './sentry';

// Initialize Resend with API key from environment
const resend = new Resend(process.env.RESEND_API_KEY);

export interface EmailParams {
  to: string | string[];
  subject: string;
  html?: string;
  text?: string;
  from?: string;
  replyTo?: string;
  cc?: string | string[];
  bcc?: string | string[];
  attachments?: Array<{
    filename: string;
    content: Buffer | string;
    contentType?: string;
  }>;
  tags?: Array<{
    name: string;
    value: string;
  }>;
}

export interface EmailResponse {
  success: boolean;
  messageId?: string;
  error?: string;
}

const DEFAULT_FROM = process.env.SMTP_FROM || 'noreply@zenith.engineer';

export async function sendEmail(params: EmailParams): Promise<EmailResponse> {
  try {
    // Validate required parameters
    if (!params.to) {
      throw new Error('Recipient email address is required');
    }

    if (!params.subject) {
      throw new Error('Email subject is required');
    }

    if (!params.html && !params.text) {
      throw new Error('Email content (html or text) is required');
    }

    // Check if Resend is properly configured
    if (!process.env.RESEND_API_KEY) {
      console.warn('RESEND_API_KEY not configured, email will not be sent');
      return { success: false, error: 'Email service not configured' };
    }

    // Send email using Resend
    const response = await resend.emails.send({
      from: params.from || DEFAULT_FROM,
      to: Array.isArray(params.to) ? params.to : [params.to],
      subject: params.subject,
      html: params.html,
      text: params.text,
      reply_to: params.replyTo,
      cc: params.cc ? (Array.isArray(params.cc) ? params.cc : [params.cc]) : undefined,
      bcc: params.bcc ? (Array.isArray(params.bcc) ? params.bcc : [params.bcc]) : undefined,
      attachments: params.attachments,
      tags: params.tags,
    });

    if (response.error) {
      throw new Error(`Resend API error: ${response.error.message}`);
    }

    console.log(`Email sent successfully to ${params.to}:`, response.data?.id);

    return {
      success: true,
      messageId: response.data?.id,
    };
  } catch (error) {
    const errorMessage = error instanceof Error ? error.message : 'Unknown email error';

    console.error('Failed to send email:', {
      error: errorMessage,
      to: params.to,
      subject: params.subject,
    });

    // Capture error in Sentry
    captureException(error as Error, {
      context: 'email-service',
      extra: {
        to: params.to,
        subject: params.subject,
        hasHtml: !!params.html,
        hasText: !!params.text,
      },
    });

    return {
      success: false,
      error: errorMessage,
    };
  }
}

// Email templates
export const EmailTemplates = {
  // Welcome email for new users
  welcome: (name: string, loginUrl: string) => ({
    subject: 'Welcome to Zenith Platform!',
