 14→
 15→export function useAnalytics(type: string = 'summary', dateRange?: { startDate: string; endDate: string }) {
 16→  const [data, setData] = useState<AnalyticsData>({ loading: true });
 17→
 18→  useEffect(() => {
 19→    fetchAnalyticsData();
 20→  }, [type, dateRange]);
 21→
