'use client';

import { useEffect, useState, lazy, Suspense } from 'react';
import Link from 'next/link';
import { motion, useScroll, useTransform } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { 
  Sparkles, 
  Zap, 
  Shield, 
  Globe, 
  TrendingUp,
  Users,
  Code2,
  Rocket,
  CheckCircle2,
  ArrowRight,
  Play,
  Star
} from 'lucide-react';

// Lazy load components below the fold
const LazyFeatureSection = lazy(() => Promise.resolve({ default: FeatureSection }));
const LazySocialProofSection = lazy(() => Promise.resolve({ default: SocialProofSection }));
const LazyFinalCTA = lazy(() => Promise.resolve({ default: FinalCTA }));

// Glassmorphic Components
const GlassCard = ({ children, className = '', delay = 0 }: any) => (
  <motion.div
    initial={{ opacity: 0, y: 20 }}
    animate={{ opacity: 1, y: 0 }}
    transition={{ duration: 0.5, delay }}
    className={`backdrop-blur-xl bg-white/5 border border-white/10 rounded-3xl p-8 shadow-2xl ${className}`}
  >
    {children}
  </motion.div>
);

// Animated counter for metrics
const AnimatedCounter = ({ end, duration = 2, suffix = '' }: any) => {
  const [count, setCount] = useState(0);
  const [ref, inView] = useInView({ triggerOnce: true });

  useEffect(() => {
    if (inView) {
      let start = 0;
      const increment = end / (duration * 60);
      const timer = setInterval(() => {
        start += increment;
        if (start >= end) {
          setCount(end);
          clearInterval(timer);
        } else {
          setCount(Math.floor(start));
        }
      }, 1000 / 60);
      return () => clearInterval(timer);
    }
  }, [inView, end, duration]);

  return <span ref={ref}>{count.toLocaleString()}{suffix}</span>;
};

