  analyzeStructuredData(html) {
    const jsonLd = (html.match(/<script[^>]+type=["\']application\/ld\+json["\'][^>]*>/gi) || []).length;
    const microdata = /itemscope|itemtype|itemprop/gi.test(html);

    return {
