                        <div className="flex-shrink-0 flex space-x-2">
                          <span className="text-lg">{getTypeIcon(notification.type)}</span>
                          <span className="text-sm">{getCategoryIcon(notification.category)}</span>
                        </div>

                        {/* Content */}
                        <div className="flex-1 min-w-0">
