'use client';

import { useState, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

interface Task {
  id: string;
  title: string;
  description: string;
  assignee: {
    name: string;
    avatar: string;
    email: string;
  };
  priority: 'low' | 'medium' | 'high' | 'urgent';
  tags: string[];
  dueDate?: Date;
  createdAt: Date;
  updatedAt: Date;
  comments: number;
  attachments: number;
}

interface Column {
  id: string;
  title: string;
  color: string;
  tasks: Task[];
  limit?: number;
}

interface KanbanBoardProps {
  projectId: string;
  className?: string;
}

export function KanbanBoard({ projectId, className = '' }: KanbanBoardProps) {
  const [columns, setColumns] = useState<Column[]>([
    {
      id: 'backlog',
      title: 'Backlog',
      color: 'bg-gray-100',
      tasks: [
        {
          id: '1',
          title: 'Design homepage wireframes',
          description: 'Create initial wireframes for the homepage layout including hero section, navigation, and footer.',
          assignee: {
            name: 'John Doe',
            avatar: '/api/placeholder/32/32',
            email: 'john@company.com'
          },
          priority: 'medium',
          tags: ['design', 'wireframes'],
          dueDate: new Date(Date.now() + 7 * 24 * 60 * 60 * 1000),
          createdAt: new Date(Date.now() - 24 * 60 * 60 * 1000),
          updatedAt: new Date(Date.now() - 2 * 60 * 60 * 1000),
          comments: 3,
          attachments: 1
        },
        {
          id: '2',
          title: 'Set up authentication system',
          description: 'Implement user registration, login, and password reset functionality.',
          assignee: {
            name: 'Jane Smith',
            avatar: '/api/placeholder/32/32',
